INSERT INTO `wp_postmeta`(post_id, meta_key, meta_value) VALUES (3724,'ru_teacher_col_1','“When I left Tibet and settled in the West, I realised that the Dzogchen Teaching was a vast body of knowledge that could help us live our condition, as human beings, better, beyond any cultural context, allowing for a natural spiritual and social evolution.” \r\n\r\n<em>Chögyal Namkhai Norbu</em>');
INSERT INTO `wp_postmeta`(post_id, meta_key, meta_value) VALUES (3724,'ru_teacher_col_2','<h6>About the Teacher</h6>\r\n<p>Today, Chogyal Namkhai Norbu is the supreme present-day representative of the tradition of the Dzogchen (Great Perfection) Teachings, an ancient teaching which can help to direct people towards a natural condition of non-violence, compassion and kindness.</p>');
INSERT INTO `wp_postmeta`(post_id, meta_key, meta_value) VALUES (3724,'ru_teacher_col_4','<h6>The message</h6>\r\n<p>The message delivered by the Master for 50 years is that it is possible to live harmoniously, even in the midst of chaotic, modern daily life and lead a lifestyle that is relaxed, collaborative and peaceful.\r\n<br><br>\r\nThe full biography can be found <u><a href=\"http://www.dzogchen.it/chogyal-namkhai-norbu/#\">here</a> </u></p>');
INSERT INTO `wp_postmeta`(post_id, meta_key, meta_value) VALUES (3724,'ru_teacher_h','The teacher who introduced Dzogchen to the Western world');
INSERT INTO `wp_postmeta`(post_id, meta_key, meta_value) VALUES (3724,'ru_teacher_h3','Chögyal Namkhai Norbu');
INSERT INTO `wp_postmeta`(post_id, meta_key, meta_value) VALUES (3724,'ru_teacher_p','<i>“When I left Tibet and settled in the West, I realised that the Dzogchen Teaching was a vast body of knowledge that could help us live our condition, as human beings, better, beyond any cultural context, allowing for a “natural” spiritual and social evolution.” </i><br><br>\r\n\r\nToday, Master Chogyal Namkhai Norbu is the supreme present-day representative of the tradition of the Dzogchen (Great Perfection) Teachings, an ancient teaching which can help to direct people towards a natural condition of non-violence, compassion and kindness.<br><br>\r\n\r\nThe message delivered by the Master for 50 years is that it is possible to live harmoniously, even in the midst of chaotic, modern daily life and lead a lifestyle that is relaxed, collaborative and peaceful.');
